import React from 'react'

const Work = (props) => {
  const erase=(id)=>
{
    listTask((pval)=>{
    return pval.filter((arrElem,index)=>{
      return index !== id;
    });
});
}

  return (
    <>
    <h1>{props.text}</h1>
    <button onClick={erase(props.id)}>delete</button>
    
    </>

  )
}

export default Work;
