import React,{ useState } from "react";

const App=()=>{

const[fullname,setName]=useState({
    fname:"",
    adrs:"",
});

const[fnlname,setfnlname]=useState({
    nm:"",
    ad:"",
});


const disp=(event)=>{
    const{value,name}=event.target;
    
 setName((preval)=>
{
    if(name==='fname')
    {
        return{
        fname:value,
        adrs:preval.adrs,
        };
    }
    else
    {
        return{
        fname:preval.fname,
        adrs:value,
        };
    }
})}

const show=()=>
{
   setfnlname((prevl)=>
   {
    return{
    nm:fullname.fname,
    ad:fullname.adrs,}
   })
 
}

 return(
<>
<h1>Hello {fullname.fname} {fullname.adrs}</h1>
<h2>chill my boy !!!!</h2>
<br/>
<input type="text" onChange={disp} placeholder="enter name" name="fname" value={fullname.fname}/>
<input type="text" onChange={disp} placeholder="enter address" name="adrs" value={fullname.adrs}/>
<button onClick={show} >Click me:ðŸ™‚</button>
</>
);
}

export default App;

