import React, { useState } from 'react'

const TDL = () => {
let a=1;
const[data,setData]=useState("");
const[task,listTask]=useState([]);

const set=(event)=>
{
    setData(event.target.value);
}

const show=()=>
{
   listTask((preval)=>{
    return[...preval,data];
   })

}

const erase=()=>
{
   alert("clicked");
}

  return (
    <>
    <input onChange={set}></input>
    <button onClick={show}>+</button>
    {task.map((val)=>
    {
        return(
        <>
        <h1 id={a++}>{val}</h1>
        <button onClick={erase}>-</button>
        </>
        );
        

    })
    
    }
    </>
  )
}

export default TDL;
